function [x,fx,k] = steep(x, f, g)

kmax = 1e4;
alpha = 0.5;
eps = 1e-4;

fx = f(x);
gx = g(x);

k = 1;
while norm(gx) > eps
  t = 1;
  while f(x-t*gx) > fx - alpha*t*dot(gx,gx)
    t = t*0.9;
  end
  x = x - t*gx;
  fx = f(x);
  gx = g(x);
  k = k + 1;
  if k > kmax
    return
  end
end

